import React, { useState } from "react";

export default function Booking() {
  const [idioma, setIdioma] = useState("español");
  const [nombre, setNombre] = useState("");
  const [email, setEmail] = useState("");
  const [fecha, setFecha] = useState("");
  const [hora, setHora] = useState("");

  const enviarFormulario = (e) => {
    e.preventDefault();
    alert("✅ Reserva enviada:\n" +
      "Nombre: " + nombre + "\n" +
      "Email: " + email + "\n" +
      "Idioma: " + idioma + "\n" +
      "Fecha: " + fecha + " " + hora);
  };

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-6">Reserva tu clase</h2>
      <form onSubmit={enviarFormulario} className="grid gap-4">
        <label className="block">
          <span className="font-semibold">Idioma:</span>
          <select value={idioma} onChange={(e) => setIdioma(e.target.value)} className="w-full border mt-1 p-2 rounded">
            <option value="español">Español</option>
            <option value="francés">Francés</option>
            <option value="árabe">Árabe</option>
          </select>
        </label>
        <label className="block">
          <span className="font-semibold">Nombre:</span>
          <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} className="w-full border p-2 rounded" required />
        </label>
        <label className="block">
          <span className="font-semibold">Correo electrónico:</span>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} className="w-full border p-2 rounded" required />
        </label>
        <label className="block">
          <span className="font-semibold">Fecha:</span>
          <input type="date" value={fecha} onChange={(e) => setFecha(e.target.value)} className="w-full border p-2 rounded" required />
        </label>
        <label className="block">
          <span className="font-semibold">Hora:</span>
          <input type="time" value={hora} onChange={(e) => setHora(e.target.value)} className="w-full border p-2 rounded" required />
        </label>
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Reservar sesión
        </button>
      </form>
    </div>
  );
}
