import React from "react";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";
import LanguageSelector from "./LanguageSelector";

export default function Navbar() {
  const { t } = useTranslation();

  return (
    <nav className="bg-blue-700 text-white px-6 py-3 shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        {/* Logo */}
        <Link to="/" className="text-xl font-bold hover:text-yellow-300">
          üåç NEO BLA BLA
        </Link>

        {/* Navegaci√≥n principal */}
        <div className="flex flex-wrap items-center gap-3 text-sm">
          <Link to="/lecciones" className="bg-blue-600 px-3 py-1 rounded hover:bg-yellow-300 hover:text-black">{t("lecciones")}</Link>
          <Link to="/reserva" className="bg-blue-600 px-3 py-1 rounded hover:bg-yellow-300 hover:text-black">{t("reservar")}</Link>
          <Link to="/usuarios" className="bg-blue-600 px-3 py-1 rounded hover:bg-yellow-300 hover:text-black">{t("usuarios")}</Link>
          <Link to="/registro" className="bg-blue-600 px-3 py-1 rounded hover:bg-yellow-300 hover:text-black">{t("registro")}</Link>
          <Link to="/login" className="bg-blue-600 px-3 py-1 rounded hover:bg-yellow-300 hover:text-black">{t("login")}</Link>
          <Link to="/chat" className="bg-blue-600 px-3 py-1 rounded hover:bg-yellow-300 hover:text-black">{t("chat")}</Link>
          <Link to="/progreso" className="bg-blue-600 px-3 py-1 rounded hover:bg-yellow-300 hover:text-black">{t("progreso")}</Link>
          <Link to="/admin-reservas" className="bg-blue-600 px-3 py-1 rounded hover:bg-yellow-300 hover:text-black">{t("admin")}</Link>
        </div>

        {/* Secci√≥n secundaria: contacto, info, idioma */}
        <div className="hidden md:flex items-center gap-3 text-xs text-gray-200 ml-4">
          <Link to="/contacto" className="hover:text-yellow-300">{t("contacto")}</Link>
          <Link to="/faq" className="hover:text-yellow-300">{t("faq")}</Link>
          <Link to="/politica-privacidad" className="hover:text-yellow-300">{t("politica_privacidad")}</Link>
          <Link to="/terminos-servicio" className="hover:text-yellow-300">{t("terminos_servicio")}</Link>
          <Link to="/sobre-nosotros" className="hover:text-yellow-300">{t("sobre_nosotros")}</Link>
          <LanguageSelector />
        </div>
      </div>
    </nav>
  );
}
