import React, { useState } from "react";

const lessons = {
  español: {
    principiante: [
      { tema: "Saludos", frase: "Hola, ¿cómo estás?", traduccion: "Hello, how are you?" },
      { tema: "Despedida", frase: "Adiós, hasta luego.", traduccion: "Goodbye, see you later." }
    ],
    intermedio: [
      { tema: "Viajes", frase: "¿Dónde está el aeropuerto?", traduccion: "Where is the airport?" },
      { tema: "Comida", frase: "Quisiera una ensalada, por favor.", traduccion: "I would like a salad, please." }
    ],
    avanzado: [
      { tema: "Trabajo", frase: "Estoy buscando oportunidades laborales.", traduccion: "I'm looking for job opportunities." },
      { tema: "Cultura", frase: "La diversidad cultural enriquece a la sociedad.", traduccion: "Cultural diversity enriches society." }
    ]
  },
  francés: {
    principiante: [
      { tema: "Saludos", frase: "Bonjour, comment ça va ?", traduccion: "Hello, how are you?" },
      { tema: "Despedida", frase: "Au revoir, à bientôt.", traduccion: "Goodbye, see you soon." }
    ],
    intermedio: [
      { tema: "Viajes", frase: "Où est la gare ?", traduccion: "Where is the train station?" },
      { tema: "Comida", frase: "Je voudrais une soupe, s'il vous plaît.", traduccion: "I would like a soup, please." }
    ],
    avanzado: [
      { tema: "Trabajo", frase: "Je cherche de nouvelles opportunités professionnelles.", traduccion: "I'm looking for new professional opportunities." },
      { tema: "Cultura", frase: "La diversité culturelle enrichit la société.", traduccion: "Cultural diversity enriches society." }
    ]
  },
  árabe: {
    principiante: [
      { tema: "Saludos", frase: "مرحبا، كيف حالك؟", traduccion: "Hello, how are you?", transcripcion: "Marhaban, kayfa ḥāluka/ḥāluki?" },
      { tema: "Despedida", frase: "مع السلامة", traduccion: "Goodbye", transcripcion: "Maʿa as-salāma" }
    ],
    intermedio: [
      { tema: "Viajes", frase: "أين المطار؟", traduccion: "Where is the airport?", transcripcion: "Ayna al-maṭār?" },
      { tema: "Comida", frase: "أريد سلطة، من فضلك.", traduccion: "I want a salad, please.", transcripcion: "Urīdu salaṭa, min faḍlik." }
    ],
    avanzado: [
      { tema: "Trabajo", frase: "أبحث عن فرص عمل.", traduccion: "I'm looking for job opportunities.", transcripcion: "Abḥathu ʿan furaṣ ʿamal." },
      { tema: "Cultura", frase: "التنوع الثقافي يثري المجتمع.", traduccion: "Cultural diversity enriches society.", transcripcion: "At-tanawwuʿ ath-thaqāfī yuthrī al-mujtamaʿ." }
    ]
  }
};

export default function Lessons() {
  const [idioma, setIdioma] = useState("español");
  const [nivel, setNivel] = useState("principiante");

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Lecciones por Idioma y Nivel</h2>
      <div className="flex gap-4 mb-4">
        {Object.keys(lessons).map((lang) => (
          <button
            key={lang}
            className={"px-4 py-2 rounded " + (idioma === lang ? "bg-blue-600 text-white" : "bg-gray-200")}
            onClick={() => setIdioma(lang)}
          >
            {lang}
          </button>
        ))}
      </div>
      <div className="flex gap-4 mb-6">
        {["principiante", "intermedio", "avanzado"].map((niv) => (
          <button
            key={niv}
            className={"px-4 py-2 rounded " + (nivel === niv ? "bg-green-600 text-white" : "bg-gray-200")}
            onClick={() => setNivel(niv)}
          >
            {niv}
          </button>
        ))}
      </div>
      <div className="grid gap-4">
        {lessons[idioma][nivel].map((item, index) => (
          <div key={index} className="p-4 border rounded bg-white shadow">
            <h3 className="text-xl font-semibold">{item.tema}</h3>
            <p className="mt-2"><strong>Frase:</strong> {item.frase}</p>
            <p><strong>Traducción:</strong> {item.traduccion}</p>
            {item.transcripcion && <p><strong>Transcripción:</strong> {item.transcripcion}</p>}
          </div>
        ))}
      </div>
    </div>
  );
}
