import React from "react";
import { Link } from "react-router-dom";
import LanguageSelector from "./LanguageSelector";
import { useTranslation } from "react-i18next";

export default function Navbar() {
  const { t } = useTranslation();

  return (
    <nav className="bg-blue-700 text-white px-4 py-3 shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto flex flex-col md:flex-row md:justify-between md:items-center gap-2 md:gap-0">
        {/* Logo */}
        <div className="flex justify-between items-center">
          <Link to="/" className="font-bold text-lg hover:text-yellow-300">
            üåç NEO BLA BLA
          </Link>
        </div>

        {/* Navegaci√≥n principal */}
        <div className="flex flex-wrap justify-center gap-4 text-base mt-2 md:mt-0">
          <Link to="/lecciones" className="hover:text-yellow-300">{t("lecciones")}</Link>
          <Link to="/reserva" className="hover:text-yellow-300">{t("reservar")}</Link>
          <Link to="/chat" className="hover:text-yellow-300">{t("chat")}</Link>
          <Link to="/progreso" className="hover:text-yellow-300">{t("progreso")}</Link>
          <Link to="/admin-reservas" className="hover:text-yellow-300">{t("admin")}</Link>
        </div>

        {/* Enlaces secundarios y accesibilidad */}
        <div className="flex flex-wrap justify-center gap-3 text-sm mt-2 md:mt-0 text-gray-200">
          <Link to="/registro" className="hover:text-yellow-300">{t("registro")}</Link>
          <Link to="/login" className="hover:text-yellow-300">{t("login")}</Link>
          <Link to="/contacto" className="hover:text-yellow-300">{t("contacto")}</Link>
          <Link to="/faq" className="hover:text-yellow-300">{t("faq")}</Link>
          <Link to="/politica-privacidad" className="hover:text-yellow-300">{t("politica_privacidad")}</Link>
          <Link to="/terminos-servicio" className="hover:text-yellow-300">{t("terminos_servicio")}</Link>
          <Link to="/sobre-nosotros" className="hover:text-yellow-300">{t("sobre_nosotros")}</Link>
        </div>

        {/* Selector de idioma */}
        <div className="flex items-center justify-center gap-2 mt-3 md:mt-0 text-sm text-gray-300">
          <span>{t("idioma")}:</span>
          <LanguageSelector />
        </div>
      </div>
    </nav>
  );
}
