import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';

const resources = {
  es: {
    translation: {
      bienvenida: "Bienvenido a NEO BLA BLA",
      lecciones: "Lecciones",
      descripcion: "Tu plataforma inteligente para aprender idiomas de forma práctica y divertida",
      intro: "Aprende Español, Francés y Árabe con IA, profesores en vivo, ejercicios prácticos y progreso visual.",
      reservar: "Reservar",
      chat: "Chat IA",
      progreso: "Progreso",
      admin: "Administración"

    }
  },
  fr: {
    translation: {
      bienvenida: "Bienvenue sur NEO BLA BLA",
      lecciones: "Leçons",
      reservar: "Réserver",
      chat: "Chat IA",
      progreso: "Progrès",
      admin: "Administration"
    }
  },
  ar: {
    translation: {
      bienvenida: "مرحبًا بك في نيو بلا بلا",
      lecciones: "دروس",
      reservar: "احجز",
      chat: "دردشة الذكاء الاصطناعي",
      progreso: "التقدم",
      admin: "لوحة الإدارة"
    }
  }
};

i18n
  .use(initReactI18next)
  .init({
    resources,
    lng: 'es',
    fallbackLng: 'es',
    interpolation: {
      escapeValue: false
    }
  });

export default i18n;
